@@ -0,0 +1,171 @@
+package com.vantinh.tienganh;
+
+import android.os.Bundle;
+import android.widget.Button;
+import android.widget.RadioButton;
+import android.widget.RadioGroup;
+import android.widget.TextView;
+import android.widget.Toast;
+
+import androidx.appcompat.app.AppCompatActivity;
+
+import java.util.ArrayList;
+import java.util.List;
+
+public class QuizActivity extends AppCompatActivity {
+
+    private TextView tvQuestion, tvQuestionNumber, tvTimer;
+    private RadioGroup rgOptions;
+    private RadioButton rbOption1, rbOption2, rbOption3, rbOption4;
+    private Button btnNext, btnPrevious, btnSubmit;
+
+    private List<Question> questionList;
+    private int currentQuestionIndex = 0;
+    private int[] userAnswers;
+    private int score = 0;
+
+    @Override
+    protected void onCreate(Bundle savedInstanceState) {
+        super.onCreate(savedInstanceState);
+        setContentView(R.layout.activity_quiz);
+
+        initViews();
+        loadQuestions();
+        setupClickListeners();
+        displayQuestion();
+    }
+
+    private void initViews() {
+        tvQuestion = findViewById(R.id.tv_question);
+        tvQuestionNumber = findViewById(R.id.tv_question_number);
+        tvTimer = findViewById(R.id.tv_timer);
+        rgOptions = findViewById(R.id.rg_options);
+        rbOption1 = findViewById(R.id.rb_option1);
+        rbOption2 = findViewById(R.id.rb_option2);
+        rbOption3 = findViewById(R.id.rb_option3);
+        rbOption4 = findViewById(R.id.rb_option4);
+        btnNext = findViewById(R.id.btn_next);
+        btnPrevious = findViewById(R.id.btn_previous);
+        btnSubmit = findViewById(R.id.btn_submit);
+    }
+
+    private void loadQuestions() {
+        // TODO: Load questions from database
+        questionList = new ArrayList<>();
+        
+        // Sample questions
+        questionList.add(new Question("What is the past tense of 'go'?", 
+                new String[]{"goed", "went", "gone", "going"}, 1));
+        questionList.add(new Question("Which article is used before vowel sounds?", 
+                new String[]{"a", "an", "the", "no article"}, 1));
+        questionList.add(new Question("What does 'beautiful' mean?", 
+                new String[]{"ugly", "pretty", "big", "small"}, 1));
+        questionList.add(new Question("Choose the correct sentence:", 
+                new String[]{"I am go to school", "I go to school", "I going to school", "I goes to school"}, 1));
+        questionList.add(new Question("What is the plural of 'child'?", 
+                new String[]{"childs", "childes", "children", "child"}, 2));
+
+        userAnswers = new int[questionList.size()];
+        for (int i = 0; i < userAnswers.length; i++) {
+            userAnswers[i] = -1; // -1 means no answer selected
+        }
+    }
+
+    private void setupClickListeners() {
+        btnNext.setOnClickListener(v -> {
+            saveCurrentAnswer();
+            if (currentQuestionIndex < questionList.size() - 1) {
+                currentQuestionIndex++;
+                displayQuestion();
+            }
+        });
+
+        btnPrevious.setOnClickListener(v -> {
+            saveCurrentAnswer();
+            if (currentQuestionIndex > 0) {
+                currentQuestionIndex--;
+                displayQuestion();
+            }
+        });
+
+        btnSubmit.setOnClickListener(v -> {
+            saveCurrentAnswer();
+            calculateScore();
+            showResult();
+        });
+    }
+
+    private void displayQuestion() {
+        Question currentQuestion = questionList.get(currentQuestionIndex);
+        
+        tvQuestion.setText(currentQuestion.getQuestion());
+        tvQuestionNumber.setText("Question " + (currentQuestionIndex + 1) + " of " + questionList.size());
+        
+        rbOption1.setText(currentQuestion.getOptions()[0]);
+        rbOption2.setText(currentQuestion.getOptions()[1]);
+        rbOption3.setText(currentQuestion.getOptions()[2]);
+        rbOption4.setText(currentQuestion.getOptions()[3]);
+
+        // Clear previous selection
+        rgOptions.clearCheck();
+        
+        // Restore user's previous answer if any
+        if (userAnswers[currentQuestionIndex] != -1) {
+            switch (userAnswers[currentQuestionIndex]) {
+                case 0: rbOption1.setChecked(true); break;
+                case 1: rbOption2.setChecked(true); break;
+                case 2: rbOption3.setChecked(true); break;
+                case 3: rbOption4.setChecked(true); break;
+            }
+        }
+
+        // Update button visibility
+        btnPrevious.setEnabled(currentQuestionIndex > 0);
+        btnNext.setEnabled(currentQuestionIndex < questionList.size() - 1);
+        btnSubmit.setVisibility(currentQuestionIndex == questionList.size() - 1 ? 
+                android.view.View.VISIBLE : android.view.View.GONE);
+    }
+
+    private void saveCurrentAnswer() {
+        int selectedId = rgOptions.getCheckedRadioButtonId();
+        if (selectedId != -1) {
+            if (selectedId == R.id.rb_option1) userAnswers[currentQuestionIndex] = 0;
+            else if (selectedId == R.id.rb_option2) userAnswers[currentQuestionIndex] = 1;
+            else if (selectedId == R.id.rb_option3) userAnswers[currentQuestionIndex] = 2;
+            else if (selectedId == R.id.rb_option4) userAnswers[currentQuestionIndex] = 3;
+        }
+    }
+
+    private void calculateScore() {
+        score = 0;
+        for (int i = 0; i < questionList.size(); i++) {
+            if (userAnswers[i] == questionList.get(i).getCorrectAnswer()) {
+                score++;
+            }
+        }
+    }
+
+    private void showResult() {
+        String result = "Quiz Completed!\nScore: " + score + "/" + questionList.size();
+        Toast.makeText(this, result, Toast.LENGTH_LONG).show();
+        // TODO: Save result to database and navigate to results screen
+        finish();
+    }
+
+    // Inner class for Question model
+    private static class Question {
+        private String question;
+        private String[] options;
+        private int correctAnswer;
+
+        public Question(String question, String[] options, int correctAnswer) {
+            this.question = question;
+            this.options = options;
+            this.correctAnswer = correctAnswer;
+        }
+
+        public String getQuestion() { return question; }
+        public String[] getOptions() { return options; }
+        public int getCorrectAnswer() { return correctAnswer; }
+    }
+}

