+
+    private void clearLectureFields() {
+        etLectureTitle.setText("");
+        etLectureDescription.setText("");
+        etLectureContent.setText("");
+        spLectureCourse.setSelection(0);
+        spLectureLevel.setSelection(0);
+    }
