+    private void clearQuizFields() {
+        etQuizTitle.setText("");
+        etQuizDescription.setText("");
+        etQuestion.setText("");
+        etOption1.setText("");
+        etOption2.setText("");
+        etOption3.setText("");
+        etOption4.setText("");
